const t=new Set;export const scopingAttribute="shady-unscoped";export function processUnscopedStyle(e){const n=e.textContent;if(!t.has(n)){t.add(n);const e=document.createElement("style");e.setAttribute("shady-unscoped",""),e.textContent=n,document.head.appendChild(e)}}export function isUnscopedStyle(t){return t.hasAttribute("shady-unscoped")}